void runEpaper()
{
    if(init)
    {
        // EPD_test();

        epdClear();

        if((BlackImage = (uint8_t *)malloc(Imagesize)) == NULL) {
            printf("Failed to apply for black memory...\r\n");
        }

        Paint_NewImage(BlackImage, EPD_WIDTH, EPD_HEIGHT, 90, WHITE);
        Paint_Clear(WHITE);

        // for(uint32_t i = 0; i < 37888; i++)
        // {
        //     BlackImage[i] = bmsTestImage[i];
        // }

        epdDisplay(BlackImage);

        // epdFullRefresh();
        init = false;
    }
    else
    {
        if((HAL_GetTick() - lastDataUpdate) > DATA_REFESH_PERIOD_MS)
        {
            lastDataUpdate = HAL_GetTick();

            if(numUpdates == NUM_PARTIAL_REFRESH_BEFORE_FULL_UPDATE)
            {
                // epdFullRefresh();

                epdDisplay(BlackImage);

                
                numUpdates = 0;
            }
            else
            {
                // epdPopulateData(&gBms);

                Paint_SelectImage(BlackImage);


                Paint_ClearWindows(151, 16, 151 + Font20.Width * 2, 16 + Font20.Height, WHITE);
                Paint_DrawNum(151, 16, numUpdates, &Font20, BLACK, WHITE);

                epdDisplayPartial(BlackImage);

                numUpdates++;
            }
        }
    }
}